import React, { useState } from 'react';
import Login from './components/Login';
import Register from './components/Register';
import Dashboard from './components/Dashboard';
import './App.css';

function App() {
  const [page, setPage] = useState<'login' | 'register' | 'dashboard'>('login');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');

  const handleRegisterSuccess = (name: string) => {
    setUsername(name);
    setIsLoggedIn(true);
    setPage('dashboard');
  };

  const handleLoginSuccess = (name: string) => {
    setUsername(name);
    setIsLoggedIn(true);
    setPage('dashboard');
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername('');
    setPage('login');
  };

  return (
    <div className="h-full">
      <div className="max-w-md w-full p-6 bg-white rounded-lg shadow-lg h-full flex flex-col">
        <h1 className="text-2xl font-bold text-center text-gray-800 mb-6 flex-shrink-0">听声无恙 - 声纹识别系统</h1>
        
        <div className="flex-grow overflow-y-auto">
          {!isLoggedIn ? (
            <>
              <div className="flex mb-4 flex-shrink-0">
                <button
                  className={`flex-1 py-2 text-center ${page === 'login' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800'} rounded-l-lg`}
                  onClick={() => setPage('login')}
                >
                  登录
                </button>
                <button
                  className={`flex-1 py-2 text-center ${page === 'register' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800'} rounded-r-lg`}
                  onClick={() => setPage('register')}
                >
                  注册
                </button>
              </div>
              
              <div className="mt-4">
                {page === 'login' ? (
                  <Login onLoginSuccess={handleLoginSuccess} />
                ) : (
                  <Register onRegisterSuccess={handleRegisterSuccess} />
                )}
              </div>
            </>
          ) : (
            <Dashboard username={username} onLogout={handleLogout} />
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
