import React, { useState, useRef } from 'react';

interface RegisterProps {
  onRegisterSuccess: (username: string) => void;
}

const Register: React.FC<RegisterProps> = ({ onRegisterSuccess }) => {
  const [username, setUsername] = useState('');
  const [randomDigits, setRandomDigits] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const [isEnrolling, setIsEnrolling] = useState(false);
  const [enrollmentProgress, setEnrollmentProgress] = useState(0);
  const timerRef = useRef<number | null>(null);
  const progressRef = useRef<number | null>(null);

  const generateRandomDigits = () => {
    let digits = '';
    for (let i = 0; i < 8; i++) {
      digits += Math.floor(Math.random() * 10);
    }
    return digits;
  };

  const handleStart = () => {
    if (!username) {
      alert('请输入用户名');
      return;
    }
    
    setRandomDigits(generateRandomDigits());
    setIsRecording(true);
    setRecordingTime(0);
    
    // 模拟录音计时
    timerRef.current = window.setInterval(() => {
      setRecordingTime(prev => prev + 1);
    }, 1000);
  };

  const handleStop = () => {
    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }
    
    setIsRecording(false);
    setIsEnrolling(true);
    setEnrollmentProgress(0);
    
    // 模拟声纹注册进度
    progressRef.current = window.setInterval(() => {
      setEnrollmentProgress(prev => {
        if (prev >= 100) {
          if (progressRef.current) {
            clearInterval(progressRef.current);
            progressRef.current = null;
          }
          setTimeout(() => {
            setIsEnrolling(false);
            onRegisterSuccess(username);
          }, 500);
          return 100;
        }
        return prev + 10;
      });
    }, 300);
  };

  return (
    <div className="space-y-4">
      <div className="space-y-2">
        <label className="block text-sm font-medium text-gray-700">用户名</label>
        <input
          type="text"
          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          disabled={isRecording || isEnrolling}
        />
      </div>
      
      {!isEnrolling ? (
        <>
          {randomDigits && (
            <div className="p-4 bg-gray-100 rounded-lg text-center">
              <p className="text-lg font-medium">请朗读以下数字以注册声纹：</p>
              <p className="text-3xl font-bold tracking-wider my-2">{randomDigits}</p>
            </div>
          )}
          
          {isRecording && (
            <div className="text-center">
              <div className="inline-block px-3 py-1 rounded-full bg-red-500 text-white animate-pulse">
                录音中...{recordingTime}秒
              </div>
            </div>
          )}
          
          <div className="flex space-x-4">
            {!isRecording ? (
              <button
                className="w-full py-2 px-4 bg-blue-500 text-white rounded-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:bg-blue-300"
                onClick={handleStart}
                disabled={isEnrolling || !username}
              >
                开始录音
              </button>
            ) : (
              <button
                className="w-full py-2 px-4 bg-red-500 text-white rounded-md hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-red-500"
                onClick={handleStop}
              >
                停止录音
              </button>
            )}
          </div>
        </>
      ) : (
        <div className="space-y-3">
          <p className="text-center font-medium">声纹注册中...</p>
          <div className="w-full bg-gray-200 rounded-full h-2.5">
            <div 
              className="bg-blue-600 h-2.5 rounded-full transition-all duration-300 ease-in-out" 
              style={{ width: `${enrollmentProgress}%` }}
            ></div>
          </div>
          <p className="text-sm text-gray-500 text-center">{enrollmentProgress}%</p>
        </div>
      )}
    </div>
  );
};

export default Register;
